<?phpnamespace Application\Form;use Zend\Form\Form;use Application\Model\CadastrarAlunoFilter;use Zend\Form\Element\Text;use Zend\Form\Element\Button;class CadastrarAcademia extends Form{	public function __construct($name = null){		parent::__construct('Cadastrar Academia');		$this->setAttribute('method','POST');		$this->setAttribute('class','form-horizontal');						$this->add([			'name' => 'id',			'type' => 'Hidden'		]);		$this->add([			'name' => 'nome',			'type' => 'Text',			'options' => [                 'label' => 'Nome',             ],			 'attributes' => [				'class' => 'form-control',			]			 		]);					                $this->add([			'name' => 'cep',			'type' => 'Text',			'options' => [                 'label' => 'CEP',             ],			 'attributes' => [				'class' => 'form-control',			]		]);		                                $this->add([			'name' => 'cep',			'type' => 'Text',			'options' => [                 'label' => 'CEP',             ],			 'attributes' => [				'class' => 'form-control',			]		]);		                		$this->add([			'name' => 'rua',			'type' => 'Text',			'options' => [                 'label' => 'Rua',             ],			 'attributes' => [				'class' => 'form-control',			]		]);	                                $this->add([			'name' => 'numero',			'type' => 'Text',			'options' => [                 'label' => 'Numero',             ],			 'attributes' => [				'class' => 'form-control',			]		]);	                             $this->add(array(                'name' => 'estado',                'require' => true,                'type' => 'select',                                'options'  => array(                    'label' => 'Estado',                    'value_options' => array(                        '1' => 'SP',                        '2' => 'AC',                    ),                ),            ));		                            $this->add(array(                'name' => 'cidade',                'require' => true,                'type' => 'select',                                'options'  => [                    'label' => 'Cidade',                    'value_options' => [                        '1' => 'Campinas',                        '2' => 'São Paulo',                    ],                ],            ));		                                $this->add([			'name' => 'bairro',			'type' => 'Text',			'options' => [                 'label' => 'Bairro',             ],			 'attributes' => [				'class' => 'form-control',			]		]);		                                $this->add([			'name' => 'complemento',			'type' => 'textarea',			'options' => [                 'label' => 'Complemento',             ],			 'attributes' => [				'class' => 'form-control',			]		]);		                		$this->add([			'name' => 'submit',			'type' => 'Submit',			'options' => [				'label' => 'Enviar',			],			'attributes' => [				'class' => 'btn btn-primary',			]					]);				}}?>