<?phpnamespace Application\Form;use Zend\Form\Form;use Zend\Form\Element\Text;use Zend\Form\Element\Button;class CadastrarAlimento extends Form{	public function __construct($name = null){		parent::__construct('Cadastrar Refeição');		$this->setAttribute('method','POST');		$this->setAttribute('class','form-horizontal');						$this->add([			'name' => 'id',			'type' => 'Hidden'		]);		$this->add([			'name' => 'nome',			'type' => 'Text',			'options' => [                 'label' => 'Nome',             ],			 'attributes' => [				'class' => 'form-control',			]			 		]);					                $this->add([			'name' => 'caracteristica',			'type' => 'textarea',			'options' => [                 'label' => 'Caracteristica',             ],			 'attributes' => [				'class' => 'form-control',			]		]);		                                $this->add([			'name' => 'descricaoNutricional',			'type' => 'Text',			'options' => [                 'label' => 'Descricao Nutricional',             ],			 'attributes' => [				'class' => 'form-control',			]		]);		                		$this->add([			'name' => 'submit',			'type' => 'Submit',			'options' => [				'label' => 'Enviar',			],			'attributes' => [				'class' => 'btn btn-primary',			]					]);				}}?>