<?phpnamespace Application\Form;use Zend\Form\Form;use Application\Model\CadastrarAlunoFilter;use Zend\Form\Element\Text;use Zend\Form\Element\Button;class CadastrarAviso extends Form{	public function __construct($name = null){		parent::__construct('Cadastrar Aviso');		$this->setAttribute('method','POST');		$this->setAttribute('class','form-horizontal');						$this->add([			'name' => 'id',			'type' => 'Hidden'		]);		$this->add([			'name' => 'nome',			'type' => 'Text',			'options' => [                 'label' => 'Nome',             ],			 'attributes' => [				'class' => 'form-control',			]			 		]);					                $this->add([			'name' => 'descricao',			'type' => 'textarea',			'options' => [                 'label' => 'Descricao',             ],			 'attributes' => [				'class' => 'form-control',			]		]);		                                 $this->add([			'name' => 'aluno',			'type' => 'select',			'options' => [                            'label' => 'Aluno',                            'value_options' => [                                '0' => 'Todos',                                '1' => 'Allan',                            ]                        ],			 'attributes' => [				'class' => 'form-control',			]		]);	                		$this->add([			'name' => 'submit',			'type' => 'Submit',			'options' => [				'label' => 'Enviar',			],			'attributes' => [				'class' => 'btn btn-primary',			]					]);				}}?>