<?phpnamespace Application\Form;use Zend\Form\Form;use Zend\Form\Element\Text;use Zend\Form\Element\Button;class CadastrarFrequencia extends Form{	public function __construct($name = null){		parent::__construct('Cadastrar Frequencia');		$this->setAttribute('method','POST');		$this->setAttribute('class','form-horizontal');						$this->add([			'name' => 'id',			'type' => 'Hidden'		]);                		$this->add([			'name' => 'aluno',			'type' => 'select',			'options' => [                            'label' => 'Aluno',                            'value_options' => [                                '1' => 'Aluno 1',                                '2' => 'Aluno 2',                            ],                        ],                        'attributes' => [                               'class' => 'form-control',                        ]		]);							$this->add([			'name' => 'submit',			'type' => 'Submit',			'options' => [				'label' => 'Marcar Presença',			],			'attributes' => [				'class' => 'btn btn-primary',			]		]);				}}?>