<?phpnamespace Application\Form;use Zend\Form\Form;use Zend\Form\Element\Text;use Zend\Form\Element\Button;class CadastrarTreino extends Form{	public function __construct($name = null){		parent::__construct('Cadastrar Academia');		$this->setAttribute('method','POST');		$this->setAttribute('class','form-horizontal');						$this->add([			'name' => 'id',			'type' => 'Hidden'		]);                		$this->add([			'name' => 'nome',			'type' => 'Text',			'options' => [                            'label' => 'Nome',                        ],			'attributes' => [				'class' => 'form-control',			]			 		]);	                                $this->add([			'name' => 'dataInicio',			'type' => 'Date',			'options' => [                            'label' => 'Data Inicio',                        ],			'attributes' => [				'class' => 'form-control',			]			 		]);	                                $this->add([			'name' => 'dataFim',			'type' => 'Date',			'options' => [                            'label' => 'Data Fim',                        ],			'attributes' => [				'class' => 'form-control',			]			 		]);	                		$this->add([			'name' => 'submit',			'type' => 'Submit',			'options' => [				'label' => 'Enviar',			],			'attributes' => [				'class' => 'btn btn-primary',			]					]);				}}?>